package day03.practice;

//write a program to convert `String` data type into other data types.

public class practiceClass02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int numbername = Integer.parseInt("24");
			System.out.println("Twenty Four = " + numbername);

			float percentage = Float.parseFloat("99.9");
			System.out.println("Percentage= " + percentage);

			double pi = Double.parseDouble("3.14");
			System.out.println("pi = " + pi);

			long number = Long.parseLong("8907651234");
			System.out.println("Phone Number = " + number);
		}
	}


